package com.joycastle.gamepluginbase;import android.content.BroadcastReceiver;import android.content.ComponentName;import android.content.Context;import android.content.Intent;import android.content.pm.PackageManager;import java.util.HashMap;public class NotificationClickReceiver extends BroadcastReceiver {    public static String NOTIFICATION_EXTRA = "notification-extra";    @Override    public void onReceive(Context context, Intent intent) {        String notificationExtra        = intent.getStringExtra(NOTIFICATION_EXTRA);        HashMap extra = new HashMap();        extra.put("message", notificationExtra);        SystemUtil.getInstance().exeNotifyHandler(extra);        PackageManager packageManager = context.getPackageManager();        String packageName = context.getPackageName();        ComponentName componentName = packageManager                .getLaunchIntentForPackage(packageName).getComponent();        Intent newIntent = new Intent(Intent.ACTION_MAIN, null);        newIntent.addCategory(Intent.CATEGORY_LAUNCHER);        newIntent.setComponent(componentName);        newIntent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_RESET_TASK_IF_NEEDED);        context.startActivity(newIntent);    }}