package com.joycastle.gamepluginbase;import android.app.Notification;import android.app.NotificationManager;import android.app.PendingIntent;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.graphics.BitmapFactory;import androidx.core.app.NotificationCompat;public class NotificationPublisher extends BroadcastReceiver {    public static String NOTIFICATION_ID = "notification-id";    public static String NOTIFICATION_CHANNEL_ID = "notification-channel-id";    public static String NOTIFICATION_TITLE = "notification-title";    public static String NOTIFICATION_MSG = "notification-msg";    public static String NOTIFICATION_WHEN = "notification-when";    public static String NOTIFICATION_EXTRA = "notification-extra";    private int mBoardIndex = 1000;    @Override    public void onReceive(Context context, Intent intent) {        int notificationId              = intent.getIntExtra(NOTIFICATION_ID, 0);        String notificationChannelId    = intent.getStringExtra(NOTIFICATION_CHANNEL_ID);        String notificationTitle        = intent.getStringExtra(NOTIFICATION_TITLE);        String notificationMsg          = intent.getStringExtra(NOTIFICATION_MSG);        long notificationWhen           = intent.getLongExtra(NOTIFICATION_WHEN, 0);        Intent notifyIntent = new Intent(context, NotificationClickReceiver.class);        notifyIntent.putExtra(NOTIFICATION_EXTRA, notificationMsg);        PendingIntent pendingIntent = PendingIntent                .getBroadcast(context, this.mBoardIndex, notifyIntent, PendingIntent.FLAG_UPDATE_CURRENT);        this.mBoardIndex++;        Notification notification = new NotificationCompat                .Builder(context, notificationChannelId)                .setSmallIcon(R.mipmap.ic_launcher)                .setLargeIcon(BitmapFactory.decodeResource(context.getResources(), R.mipmap.ic_launcher))                .setContentTitle(notificationTitle)                .setContentText(notificationMsg)                .setStyle(new NotificationCompat.BigTextStyle().bigText(notificationMsg))                .setContentIntent(pendingIntent)                .setPriority(NotificationCompat.PRIORITY_HIGH)                .setWhen(notificationWhen)                .setAutoCancel(true)                .setDefaults(Notification.DEFAULT_ALL)                .setNumber(1)                .build();        NotificationManager notificationManager =                (NotificationManager) context.getSystemService(Context.NOTIFICATION_SERVICE);        notificationManager.notify(notificationId, notification);    }}